		case _WAITING:
		
			if (m_pStageEffect) m_pStageEffect->Render();
			
			if (m_pInterface) m_pInterface->Render();

			if (m_pInterface &&
				m_pInterface->m_pSelect &&
				m_pInterface->m_pSelect->m_pInfWindow)
				
				m_pInterface->m_pSelect->m_pInfWindow->RenderMessaegBox();
			
