			if (IsMyShuttleLeader())
			{
				wsprintf(buf, STRMSG_C_071006_0107, pMsg->CharacterName, STRCMD_ANI_INFLUENCE_SUBLEADER_2);
			}
			else
			{
				wsprintf(buf, STRMSG_C_071006_0106, STRCMD_ANI_INFLUENCE_SUBLEADER_2);
			}
