/// \date		2008-03-17 ~ 2007-11-22
/// \warning	
///
/// \param		
/// \return		
///////////////////////////////////////////////////////////////////////////////
BOOL CAtumApplication::IsPossibleWindowMove()
{
	DWORD dwGameState = GetGameState();
	switch (dwGameState)
	{
	case _GAME:
	{
		if (m_bCharacter
			|| (NULL == m_pShuttleChild)
			|| (!m_pShuttleChild->IsAir()))
		{
			// 땅에 있거나 캐릭터 모드면 움직일수 있다.
			return TRUE;
		}
		return m_pShuttleChild->IsUnitStop();
	}
	break;
	}
	return TRUE;
}

